const React = require('react');
const Marked = require('./Marked.js');

class Doc extends React.Component {
  render() {
    let editLink = React.createElement(
      'a',
      {
        className: 'edit-page-link button',
        href: this.props.config.editUrl + this.props.language + '/' + this.props.source,
        target: '_blank'
      },
      'Edit this Doc'
    );
    return React.createElement(
      'div',
      { className: 'post' },
      React.createElement(
        'header',
        { className: 'postHeader' },
        editLink,
        React.createElement(
          'h1',
          null,
          this.props.title
        )
      ),
      React.createElement(
        'article',
        null,
        React.createElement(
          Marked,
          null,
          this.props.content
        )
      )
    );
  }
}

module.exports = Doc;