module.exports=function(string){

var accents='\xE0\xE1\xE4\xE2\xE8'+
'\xE9\xEB\xEA\xEC\xED\xEF'+
'\xEE\xF2\xF3\xF6\xF4\xF9'+
'\xFA\xFC\xFB\xF1\xE7';

var without='aaaaeeeeiiiioooouuuunc';

var slug=string.
toString().


toLowerCase().


replace(
new RegExp('['+accents+']','g'),
function(c){return without.charAt(accents.indexOf(c));}).


replace(/\.|\(|\?/g,'').


replace(/[^a-z0-9]/g,'-').


replace(/-+/g,'-').


replace(/^-|-$/g,'');


if(/\s[.]{1,}/.test(string)){
slug+='-';
}

return slug;
};